SELECT PART.P_BRAND, SUPPLIER.S_SUPPKEY, PARTSUPP.PS_SUPPKEY, MAX(PARTSUPP.PS_COMMENT) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY GROUP BY PART.P_BRAND, SUPPLIER.S_SUPPKEY, PARTSUPP.PS_SUPPKEY HAVING MAX(PARTSUPP.PS_COMMENT) != 'OUNTS. THINLY SPECIAL ACCOUNTS CAJOLE CAREFULLY. EVEN, SPECIAL ACCOUNTS AFTER';
