SELECT CUSTOMER.C_CUSTKEY, ORDERS.O_ORDERDATE, NATION.N_COMMENT, COUNT(NATION.N_NAME) FROM NATION JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY GROUP BY CUSTOMER.C_CUSTKEY, ORDERS.O_ORDERDATE, NATION.N_COMMENT HAVING COUNT(NATION.N_NAME) > 8;
