SELECT PARTSUPP.PS_SUPPKEY, PART.P_TYPE, MAX(PARTSUPP.PS_SUPPKEY), MIN(PARTSUPP.PS_COMMENT) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY WHERE PART.P_RETAILPRICE != 915.01 AND PART.P_NAME > 'WHITE RED LACE DEEP PALE' AND PARTSUPP.PS_COMMENT >= 'GULAR PINTO BEANS MAINTAIN QUICKLY FLUFFILY REGULAR DEPOSITS. EXPRESS REQUESTS SLEEP. EVEN REQUESTS AFTER THE REGU' GROUP BY PARTSUPP.PS_SUPPKEY, PART.P_TYPE;
