SELECT PART.P_BRAND, PARTSUPP.PS_PARTKEY, LINEITEM.L_EXTENDEDPRICE, SUM(LINEITEM.L_LINENUMBER) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY GROUP BY PART.P_BRAND, PARTSUPP.PS_PARTKEY, LINEITEM.L_EXTENDEDPRICE HAVING SUM(LINEITEM.L_LINENUMBER) <= 2 ORDER BY PARTSUPP.PS_PARTKEY ASC;
