SELECT PART.P_CONTAINER, LINEITEM.L_LINESTATUS, PARTSUPP.PS_PARTKEY, MAX(PART.P_CONTAINER), MIN(PART.P_SIZE) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY GROUP BY PART.P_CONTAINER, LINEITEM.L_LINESTATUS, PARTSUPP.PS_PARTKEY ORDER BY PARTSUPP.PS_PARTKEY DESC;
