SELECT CUSTOMER.C_COMMENT, NATION.N_NAME, REGION.R_REGIONKEY, COUNT(NATION.N_NATIONKEY) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY GROUP BY CUSTOMER.C_COMMENT, NATION.N_NAME, REGION.R_REGIONKEY ORDER BY COUNT(NATION.N_NATIONKEY) DESC;
