SELECT LINEITEM.L_COMMENT, PARTSUPP.PS_SUPPLYCOST, SUPPLIER.S_NATIONKEY, AVG(SUPPLIER.S_SUPPKEY) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY GROUP BY LINEITEM.L_COMMENT, PARTSUPP.PS_SUPPLYCOST, SUPPLIER.S_NATIONKEY HAVING AVG(SUPPLIER.S_SUPPKEY) = 7 ORDER BY PARTSUPP.PS_SUPPLYCOST ASC, SUPPLIER.S_NATIONKEY ASC;
