SELECT SUPPLIER.S_ADDRESS, PARTSUPP.PS_SUPPLYCOST, AVG(PARTSUPP.PS_SUPPLYCOST) FROM PARTSUPP JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE SUPPLIER.S_ADDRESS < '1KHUGZEGWM3UA7DSYMEKYBSK' AND SUPPLIER.S_COMMENT >= '. SLYLY REGULAR PINTO BEA' AND SUPPLIER.S_ADDRESS != '89EJ5KSX3IMXJQBVXOBC,' AND SUPPLIER.S_SUPPKEY < 3 AND SUPPLIER.S_NATIONKEY != 10 AND PARTSUPP.PS_SUPPKEY > 829 GROUP BY SUPPLIER.S_ADDRESS, PARTSUPP.PS_SUPPLYCOST HAVING AVG(PARTSUPP.PS_SUPPLYCOST) >= 115.89 ORDER BY PARTSUPP.PS_SUPPLYCOST DESC;
