SELECT LINEITEM.L_SHIPMODE, PARTSUPP.PS_COMMENT, SUPPLIER.S_NATIONKEY FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE LINEITEM.L_SHIPDATE = '1995-03-14' ORDER BY PARTSUPP.PS_COMMENT DESC, LINEITEM.L_SHIPMODE ASC, SUPPLIER.S_NATIONKEY DESC;
