SELECT PARTSUPP.PS_AVAILQTY, ORDERS.O_ORDERSTATUS, CUSTOMER.C_COMMENT, LINEITEM.L_COMMITDATE, NATION.N_NATIONKEY, PART.P_SIZE, MIN(NATION.N_NATIONKEY) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY GROUP BY PARTSUPP.PS_AVAILQTY, ORDERS.O_ORDERSTATUS, CUSTOMER.C_COMMENT, LINEITEM.L_COMMITDATE, NATION.N_NATIONKEY, PART.P_SIZE HAVING MIN(NATION.N_NATIONKEY) = LINEITEM ORDER BY CUSTOMER.C_COMMENT ASC, PARTSUPP.PS_AVAILQTY ASC, PART.P_SIZE ASC, ORDERS.O_ORDERSTATUS ASC;
