SELECT PARTSUPP.PS_SUPPLYCOST, NATION.N_NATIONKEY, REGION.R_NAME, SUPPLIER.S_ACCTBAL, COUNT(NATION.N_NATIONKEY) FROM PARTSUPP JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY GROUP BY PARTSUPP.PS_SUPPLYCOST, NATION.N_NATIONKEY, REGION.R_NAME, SUPPLIER.S_ACCTBAL ORDER BY SUPPLIER.S_ACCTBAL ASC, NATION.N_NATIONKEY DESC, PARTSUPP.PS_SUPPLYCOST DESC;
