SELECT ORDERS.O_SHIPPRIORITY, LINEITEM.L_RECEIPTDATE, SUPPLIER.S_COMMENT, PARTSUPP.PS_PARTKEY FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY ORDER BY LINEITEM.L_RECEIPTDATE DESC, ORDERS.O_SHIPPRIORITY ASC;
