SELECT NATION.N_REGIONKEY, CUSTOMER.C_COMMENT, REGION.R_REGIONKEY, MAX(REGION.R_COMMENT) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY WHERE NATION.N_REGIONKEY >= 56219.04 GROUP BY NATION.N_REGIONKEY, CUSTOMER.C_COMMENT, REGION.R_REGIONKEY HAVING MAX(REGION.R_COMMENT) < 'SUPPLIER' ORDER BY NATION.N_REGIONKEY ASC;
