SELECT NATION.N_NAME, SUPPLIER.S_ACCTBAL, COUNT(SUPPLIER.S_COMMENT) FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE SUPPLIER.S_PHONE != '24-696-997-4969' GROUP BY NATION.N_NAME, SUPPLIER.S_ACCTBAL HAVING COUNT(SUPPLIER.S_COMMENT) < 0 ORDER BY COUNT(SUPPLIER.S_COMMENT) ASC;
