SELECT PART.P_BRAND, PARTSUPP.PS_COMMENT, ORDERS.O_CLERK, LINEITEM.L_TAX FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE PART.P_NAME != 'MAGENTA BLUE CHARTREUSE TAN GREEN';
