SELECT ORDERS.O_COMMENT, CUSTOMER.C_MKTSEGMENT, AVG(ORDERS.O_CUSTKEY) FROM ORDERS, CUSTOMER WHERE ORDERS.O_COMMENT <= 'DENCIES. BLITHELY QUICK PACKAGES CAJOLE. RUTHLESSLY FINAL ACCOUNTS' GROUP BY ORDERS.O_COMMENT, CUSTOMER.C_MKTSEGMENT HAVING AVG(ORDERS.O_CUSTKEY) != 14987 ORDER BY AVG(ORDERS.O_CUSTKEY) ASC;
