SELECT LINEITEM.L_QUANTITY, PARTSUPP.PS_SUPPLYCOST, CUSTOMER.C_COMMENT, ORDERS.O_ORDERKEY FROM PARTSUPP JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY WHERE CUSTOMER.C_ACCTBAL != 1007.18 ORDER BY ORDERS.O_ORDERKEY ASC, CUSTOMER.C_COMMENT ASC;
