SELECT CUSTOMER.C_ACCTBAL, SUPPLIER.S_ADDRESS, NATION.N_COMMENT FROM SUPPLIER JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY WHERE CUSTOMER.C_PHONE < '22-603-468-3533' AND SUPPLIER.S_PHONE <= '27-498-742-3860' ORDER BY NATION.N_COMMENT ASC, CUSTOMER.C_ACCTBAL DESC, SUPPLIER.S_ADDRESS DESC;
