SELECT ORDERS.O_ORDERDATE, CUSTOMER.C_ACCTBAL, NATION.N_COMMENT, REGION.R_REGIONKEY FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY ORDER BY CUSTOMER.C_ACCTBAL ASC, ORDERS.O_ORDERDATE DESC, NATION.N_COMMENT DESC, REGION.R_REGIONKEY DESC;
