SELECT LINEITEM.L_SHIPDATE, CUSTOMER.C_COMMENT, ORDERS.O_ORDERDATE, NATION.N_REGIONKEY FROM NATION JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY WHERE LINEITEM.L_PARTKEY != 11797 ORDER BY LINEITEM.L_SHIPDATE DESC, NATION.N_REGIONKEY ASC, ORDERS.O_ORDERDATE DESC;
