SELECT CUSTOMER.C_MKTSEGMENT, ORDERS.O_ORDERKEY, NATION.N_NAME, REGION.R_NAME FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY WHERE CUSTOMER.C_NATIONKEY = 22 AND NATION.N_NAME <= '85454.10' AND ORDERS.O_ORDERDATE = '1996-12-19' ORDER BY ORDERS.O_ORDERKEY ASC;
