SELECT SUPPLIER.S_NAME, NATION.N_NAME, REGION.R_REGIONKEY, MIN(REGION.R_COMMENT) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY GROUP BY SUPPLIER.S_NAME, NATION.N_NAME, REGION.R_REGIONKEY HAVING MIN(REGION.R_COMMENT) >= 'PARTSUPP' ORDER BY MIN(REGION.R_COMMENT) ASC;
