SELECT NATION.N_NAME, CUSTOMER.C_PHONE, PARTSUPP.PS_COMMENT, SUPPLIER.S_SUPPKEY FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY WHERE NATION.N_NAME >= 'OUSLY ACCORDING TO THE SLY FOXES. BLITHELY REGULAR PINTO BEAN' ORDER BY CUSTOMER.C_PHONE ASC, PARTSUPP.PS_COMMENT DESC;
