SELECT SUPPLIER.S_NATIONKEY, LINEITEM.L_SUPPKEY, CUSTOMER.C_PHONE, ORDERS.O_ORDERDATE, NATION.N_NATIONKEY FROM SUPPLIER JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY WHERE NATION.N_NATIONKEY != 3847 ORDER BY CUSTOMER.C_PHONE ASC, ORDERS.O_ORDERDATE DESC;
