SELECT PART.P_TYPE, SUPPLIER.S_PHONE, PARTSUPP.PS_PARTKEY, NATION.N_REGIONKEY FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY WHERE NATION.N_NATIONKEY <= 35965.85 ORDER BY SUPPLIER.S_PHONE ASC;
