SELECT PARTSUPP.PS_SUPPLYCOST, ORDERS.O_CLERK, LINEITEM.L_COMMENT, CUSTOMER.C_NATIONKEY, SUPPLIER.S_SUPPKEY FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE PARTSUPP.PS_SUPPKEY >= 910;
