SELECT PART.P_NAME, PARTSUPP.PS_AVAILQTY, SUPPLIER.S_NATIONKEY, NATION.N_REGIONKEY, MIN(PART.P_CONTAINER) FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY GROUP BY PART.P_NAME, PARTSUPP.PS_AVAILQTY, SUPPLIER.S_NATIONKEY, NATION.N_REGIONKEY;
