SELECT CUSTOMER.C_ADDRESS, ORDERS.O_ORDERPRIORITY, NATION.N_REGIONKEY, REGION.R_REGIONKEY FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY ORDER BY REGION.R_REGIONKEY ASC, ORDERS.O_ORDERPRIORITY DESC, NATION.N_REGIONKEY ASC;
