SELECT ORDERS.O_ORDERSTATUS, LINEITEM.L_EXTENDEDPRICE, NATION.N_NAME, CUSTOMER.C_PHONE, REGION.R_COMMENT FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY;
