SELECT LINEITEM.L_RETURNFLAG, SUPPLIER.S_NATIONKEY, CUSTOMER.C_NAME, PARTSUPP.PS_SUPPLYCOST, ORDERS.O_ORDERSTATUS, NATION.N_REGIONKEY, REGION.R_NAME FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY;
