SELECT PARTSUPP.PS_COMMENT, SUPPLIER.S_NAME, PART.P_CONTAINER, AVG(PARTSUPP.PS_SUPPKEY) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY GROUP BY PARTSUPP.PS_COMMENT, SUPPLIER.S_NAME, PART.P_CONTAINER HAVING AVG(PARTSUPP.PS_SUPPKEY) >= 812 ORDER BY AVG(PARTSUPP.PS_SUPPKEY) DESC;
