SELECT LINEITEM.L_SHIPINSTRUCT, PARTSUPP.PS_SUPPKEY, ORDERS.O_TOTALPRICE, SUPPLIER.S_ACCTBAL FROM SUPPLIER, PARTSUPP, LINEITEM, ORDERS WHERE LINEITEM.L_SHIPMODE > 'MAIL      ' AND ORDERS.O_COMMENT != 'QUESTS AMONG THE ACCOUNTS SLEEP BOLDLY ABOUT THE REGULAR F' AND LINEITEM.L_LINENUMBER >= 6 ORDER BY LINEITEM.L_SHIPINSTRUCT DESC, SUPPLIER.S_ACCTBAL DESC;
