SELECT SUPPLIER.S_ACCTBAL, CUSTOMER.C_ADDRESS, NATION.N_COMMENT, ORDERS.O_ORDERPRIORITY FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY ORDER BY ORDERS.O_ORDERPRIORITY ASC, NATION.N_COMMENT ASC, SUPPLIER.S_ACCTBAL DESC, CUSTOMER.C_ADDRESS ASC;
