SELECT CUSTOMER.C_COMMENT, SUPPLIER.S_ADDRESS, ORDERS.O_CUSTKEY, NATION.N_NATIONKEY FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE ORDERS.O_TOTALPRICE != 48667.29 AND ORDERS.O_CLERK = 'CLERK#000000484';
