SELECT SUPPLIER.S_PHONE, NATION.N_COMMENT, MIN(SUPPLIER.S_NATIONKEY) FROM SUPPLIER JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY WHERE SUPPLIER.S_SUPPKEY > 5 AND SUPPLIER.S_COMMENT != 'FINAL ACCOUNTS. REGULAR DOLPHINS USE AGAINST THE FURIOUSLY IRONIC DECOYS. ' GROUP BY SUPPLIER.S_PHONE, NATION.N_COMMENT ORDER BY NATION.N_COMMENT DESC;
