SELECT ORDERS.O_ORDERPRIORITY, CUSTOMER.C_MKTSEGMENT, LINEITEM.L_DISCOUNT, MIN(CUSTOMER.C_ADDRESS) FROM CUSTOMER, ORDERS, LINEITEM WHERE ORDERS.O_COMMENT != '. BUSY, EXPRESS INSTRUCTION' AND ORDERS.O_CUSTKEY <= 1648 AND LINEITEM.L_EXTENDEDPRICE >= 66955.59 GROUP BY ORDERS.O_ORDERPRIORITY, CUSTOMER.C_MKTSEGMENT, LINEITEM.L_DISCOUNT ORDER BY MIN(CUSTOMER.C_ADDRESS) DESC;
