SELECT LINEITEM.L_EXTENDEDPRICE, SUPPLIER.S_NATIONKEY, CUSTOMER.C_PHONE, ORDERS.O_CUSTKEY, NATION.N_NAME FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY ORDER BY CUSTOMER.C_PHONE ASC, LINEITEM.L_EXTENDEDPRICE ASC, NATION.N_NAME DESC, SUPPLIER.S_NATIONKEY ASC;
