SELECT NATION.N_REGIONKEY, SUPPLIER.S_SUPPKEY, CUSTOMER.C_PHONE, ORDERS.O_ORDERKEY, COUNT(SUPPLIER.S_NATIONKEY) FROM SUPPLIER JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY WHERE SUPPLIER.S_SUPPKEY != 10 GROUP BY NATION.N_REGIONKEY, SUPPLIER.S_SUPPKEY, CUSTOMER.C_PHONE, ORDERS.O_ORDERKEY ORDER BY COUNT(SUPPLIER.S_NATIONKEY) ASC;
