SELECT PARTSUPP.PS_AVAILQTY, LINEITEM.L_PARTKEY, ORDERS.O_COMMENT, MIN(PARTSUPP.PS_SUPPKEY), MIN(LINEITEM.L_COMMENT), COUNT(ORDERS.O_COMMENT) FROM PARTSUPP JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY GROUP BY PARTSUPP.PS_AVAILQTY, LINEITEM.L_PARTKEY, ORDERS.O_COMMENT ORDER BY LINEITEM.L_PARTKEY DESC;
