SELECT PART.P_CONTAINER, PARTSUPP.PS_SUPPKEY, LINEITEM.L_SHIPINSTRUCT, ORDERS.O_ORDERSTATUS FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE LINEITEM.L_RETURNFLAG != 'N' AND PART.P_RETAILPRICE <= 962.06;
