SELECT PARTSUPP.PS_SUPPKEY, LINEITEM.L_RETURNFLAG, SUPPLIER.S_ACCTBAL, NATION.N_NATIONKEY FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE SUPPLIER.S_NATIONKEY > 10 ORDER BY LINEITEM.L_RETURNFLAG DESC, SUPPLIER.S_ACCTBAL ASC, NATION.N_NATIONKEY ASC;
