SELECT LINEITEM.L_TAX, ORDERS.O_COMMENT, SUPPLIER.S_ACCTBAL, PARTSUPP.PS_COMMENT, CUSTOMER.C_CUSTKEY, MIN(ORDERS.O_COMMENT) FROM SUPPLIER, PARTSUPP, LINEITEM, ORDERS, CUSTOMER GROUP BY LINEITEM.L_TAX, ORDERS.O_COMMENT, SUPPLIER.S_ACCTBAL, PARTSUPP.PS_COMMENT, CUSTOMER.C_CUSTKEY HAVING MIN(ORDERS.O_COMMENT) = ' PEARLS. BOLD ACCOUNTS ARE ALONG THE IRONIC,';
