SELECT SUPPLIER.S_COMMENT, REGION.R_NAME, NATION.N_REGIONKEY FROM SUPPLIER JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY WHERE REGION.R_NAME <= 'ULLY REGULAR EXCUSE' ORDER BY REGION.R_NAME DESC, NATION.N_REGIONKEY ASC, SUPPLIER.S_COMMENT DESC;
