SELECT LINEITEM.L_EXTENDEDPRICE, CUSTOMER.C_NAME, PARTSUPP.PS_COMMENT, ORDERS.O_ORDERKEY FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY ORDER BY LINEITEM.L_EXTENDEDPRICE DESC, PARTSUPP.PS_COMMENT ASC;
