SELECT LINEITEM.L_SHIPMODE, SUPPLIER.S_ADDRESS, ORDERS.O_ORDERPRIORITY, PARTSUPP.PS_COMMENT, MAX(PARTSUPP.PS_SUPPLYCOST) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY GROUP BY LINEITEM.L_SHIPMODE, SUPPLIER.S_ADDRESS, ORDERS.O_ORDERPRIORITY, PARTSUPP.PS_COMMENT HAVING MAX(PARTSUPP.PS_SUPPLYCOST) != 939.14 ORDER BY ORDERS.O_ORDERPRIORITY DESC;
