SELECT ORDERS.O_CLERK, LINEITEM.L_SHIPDATE, CUSTOMER.C_ACCTBAL, PARTSUPP.PS_COMMENT, MIN(ORDERS.O_ORDERKEY) FROM PARTSUPP JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY GROUP BY ORDERS.O_CLERK, LINEITEM.L_SHIPDATE, CUSTOMER.C_ACCTBAL, PARTSUPP.PS_COMMENT;
