SELECT PARTSUPP.PS_SUPPKEY, LINEITEM.L_SHIPMODE, SUPPLIER.S_COMMENT, MIN(PARTSUPP.PS_SUPPKEY) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE SUPPLIER.S_NAME <= 'SUPPLIER#000000005       ' GROUP BY PARTSUPP.PS_SUPPKEY, LINEITEM.L_SHIPMODE, SUPPLIER.S_COMMENT ORDER BY MIN(PARTSUPP.PS_SUPPKEY) ASC;
