SELECT LINEITEM.L_DISCOUNT, PARTSUPP.PS_COMMENT, ORDERS.O_SHIPPRIORITY, MIN(LINEITEM.L_COMMITDATE) FROM PARTSUPP, LINEITEM, ORDERS WHERE LINEITEM.L_DISCOUNT <= 0.04 GROUP BY LINEITEM.L_DISCOUNT, PARTSUPP.PS_COMMENT, ORDERS.O_SHIPPRIORITY ORDER BY ORDERS.O_SHIPPRIORITY DESC, PARTSUPP.PS_COMMENT DESC, LINEITEM.L_DISCOUNT DESC;
