SELECT ORDERS.O_ORDERPRIORITY, LINEITEM.L_ORDERKEY, SUPPLIER.S_NAME, PARTSUPP.PS_PARTKEY FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY WHERE PARTSUPP.PS_SUPPKEY = 298 ORDER BY SUPPLIER.S_NAME DESC, PARTSUPP.PS_PARTKEY DESC, LINEITEM.L_ORDERKEY DESC, ORDERS.O_ORDERPRIORITY DESC;
