SELECT CUSTOMER.C_PHONE, NATION.N_NAME, REGION.R_REGIONKEY, AVG(CUSTOMER.C_ACCTBAL) FROM CUSTOMER, NATION, REGION WHERE NATION.N_REGIONKEY != LD, IRONIC REQUESTS. FURIOUSLY SPECIAL PACKAGES CAJOLE FURIOUSLY ENTICING INSTRUCTIONS. AND NATION.N_NAME < '4773' GROUP BY CUSTOMER.C_PHONE, NATION.N_NAME, REGION.R_REGIONKEY ORDER BY CUSTOMER.C_PHONE DESC, REGION.R_REGIONKEY DESC, NATION.N_NAME DESC;
